// -------------------------------// adapted from Kevin T. Manley// CSE 593// -------------------------------package ResImpl;public class Hotel extends ReservableItem{		public Hotel( String location, int count, int price )		{			super( location, count, price );		}		public String getKey()		{			return Hotel.getKey( getLocation() );		}		public static String getKey( String location )		{			String s = "room-" + location  ;			return s.toLowerCase();		}				public Hotel clone(){						 int count=getCount();			 int price=getPrice();			 String location=new String(getLocation());			 return new Hotel(location,count,price);			}}